				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$${p}_{\gamma ,s,t}=1-({r}_{\phi ,s,t}+{r}_{\gamma ,s,t}\exp (-({r}_{\phi ,s,t}+{r}_{\gamma ,s,t}){\rm{\Delta }}t))/({r}_{\phi ,s,t}+{r}_{\gamma ,s,t}),$$\end{document}pγ,s,t=1−(rϕ,s,t+rγ,s,texp(−(rϕ,s,t+rγ,s,t)Δt))/(rϕ,s,t+rγ,s,t),
